mapper.select:
select

from dt_repair


    and dtname like concat("%",#{dtname},"%")


    and dtfloor like concat("%",#{dtfloor},"%")


    and dtno like concat("%",#{dtno},"%")


    and reduty like concat("%",#{reduty},"%")


    and restate = #{restate}


    and retime >=#{startTime}



    and retime  <= #{endTime}


    and uid =#{uid}


order by retime desc

select

from dt_repair


    and dtname like concat("%",#{dtname},"%")


    and dtfloor like concat("%",#{dtfloor},"%")


    and dtno like concat("%",#{dtno},"%")


    and restate = #{restate}


    and retime >=#{startTime}



    and retime  <= #{endTime}


    and reduty in (#{reduty},'无')


order by retime desc
